async function seedCategoryData() {
  try {
    const findCategory = await Category.find({});
    const findSubCategory = await SubCategory.find({});
    const findSubSubCategory = await SubSubCategory.find({});

    if (
      findCategory.length == 0 ||
      findSubCategory.length == 0 ||
      findSubSubCategory.length == 0
    ) {
      for (const category of data) {
        const insertedCategory = await Category.insertMany({
          image: category.image,
          name: category.name,
          formData: category.formData !== null ? category.formData : null,
          buyerData: category.buyerData !== null ? category.buyerData : null,
        });

        if (category.subcategories && category.subcategories.length > 0) {
          for (const subCategory of category.subcategories) {
            const insertedSubCategory = await SubCategory.insertMany({
              name: subCategory.name,
              categoryId: [insertedCategory[0]._id],
              isSubSubCategory: false,
              formData: subCategory.formData,
              buyerData: subCategory.buyerData,
            });

            if (subCategory.subsubcategories) {
              for (const subSubCategory of subCategory.subsubcategories) {
                await SubSubCategory.insertMany({
                  name: subSubCategory.name,
                  formData: subSubCategory.formData,
                  categoryId: [insertedCategory[0]._id],
                  subCategoryId: insertedSubCategory[0]._id,
                  buyerData: subSubCategory.buyerData,
                });
                insertedSubCategory[0].isSubSubCategory = true;
                await insertedSubCategory[0].save();
              }
            }
          }
        }
      }
      console.log("Data seed successfully");
    }
  } catch (error) {
    console.error("Error inserting data:", error);
  }
}

seedCategoryData();
